import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { FaCode } from 'react-icons/fa';
import Fade from 'react-reveal/Fade';

import placeholder from '../../../assets/png/placeholder.png';
import './SingleProject.css';

function SingleProject({ id, name, desc, tags, code, image, theme }) {
	const useStyles = makeStyles((t) => ({
		iconBtn: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
			width: 40,
			height: 40,
			borderRadius: 50,
			border: `2px solid ${theme.tertiary}`,
			color: theme.tertiary,
			transition: 'all 0.2s',
			'&:hover': {
				backgroundColor: theme.secondary,
				color: theme.primary,
				transform: 'scale(1.1)',
				border: `2px solid ${theme.secondary}`,
			},
		},
		icon: {
			fontSize: '1.1rem',
			transition: 'all 0.2s',
			'&:hover': {},
		},
	}));

	const classes = useStyles();

	return (
		<Fade bottom>
			<div key={id} className='singleProject' style={{ marginTop: id === 0 ? 0 : '5rem' }}>
				<div className='content-left'>
					<h2 id={name.replace(' ', '-').toLowerCase()} style={{ color: theme.tertiary, fontSize: '1.8rem' }}>
						{name}
					</h2>
					<div className='project-desc'>{desc}</div>
					<div className='project-function'>프로젝트 주요 기능</div>
					<div className='project-role'>프로젝트 담당 역할</div>
					<div className='project--showcaseBtn'>
						<a href={code} target='_blank' rel='noreferrer' className={classes.iconBtn} aria-labelledby={`${name.replace(' ', '-').toLowerCase()} ${name.replace(' ', '-').toLowerCase()}-code`}>
							<FaCode id={`${name.replace(' ', '-').toLowerCase()}-code`} className={classes.icon} aria-label='Code' />
						</a>
					</div>
				</div>
				<div className='content-right'>
					<img className='image' src={image ? image : placeholder} alt={name} />
				</div>

				{/* <div className='projectContent'>
					<img src={image ? image : placeholder} alt={name} />
					<div className='project--showcaseBtn'>
						<a href={code} target='_blank' rel='noreferrer' className={classes.iconBtn} aria-labelledby={`${name.replace(' ', '-').toLowerCase()} ${name.replace(' ', '-').toLowerCase()}-code`}>
							<FaCode id={`${name.replace(' ', '-').toLowerCase()}-code`} className={classes.icon} aria-label='Code' />
						</a>
					</div>
				</div> */}
